// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
//Main class that will be used to perform operation on our block.
contract SendMoneyWithApproval
{
    address public sender;
    address public receiver;
    address public approver;

  function deposit(address _sender, address _receiver, address _approver) external payable {
    require(msg.value > 0);
    sender = _sender;
    receiver = _receiver;
    approver = _approver;
  }

  function viewApprover() external view returns(address) {
    return(approver);
  }
   function approve() external {
    require(msg.sender == approver);
    (receiver).transfer(1);
   }
// function sendAmount() external payable {
